import React,{useState} from "react";
import "./index.css"
const App = () =>{
    const[fullname,setFullName] = useState({
        fname:"",
        uname:"",
        email:"",
        phone:"",
        password:"",
        Confirmp:""

    });
    //const[fullname,setfullName] = useState();
    //const [title,setanotherName] = useState();
    //const [lastName,setLastname] = useState();

    const inputEvent = (event) =>{
    console.log(event.target.value);
   // const value = event.target.value;
    //const name = event.target.name;
    const {value,name} = event.target; 
    setFullName((preValue) =>{
        return{
            ...preValue,
            [name]:value,
        }


    })
    
    //setName(event.target.value);
    }
    const onSubmit = (event) =>{
        event.preventDefault();
       if(fullname.password !== fullname.confirmp){
            alert("Passwords are not the same")
       }
        alert("form submitted");
        
      //  setfullName(name);
       // setLastname(title);
        
    }
    //
    //const inputsecondEvent = (event) =>{
      //  setanotherName(event.target.value);
    //}
return(
    <>
    <div className="outer_box">
    <div className="inner_box">
    <form onSubmit={onSubmit}>
        <h1> Register</h1>
        <input type="text" required
        placeholder="Full Name"
        name='fname'
        onChange={inputEvent} 
            value={fullname.fname}
        />
        <br/>
        <input type="text" required
        placeholder="User Name"
        name='uname'
        onChange={inputEvent} 
            value={fullname.uname}
        />
        <br/>
          <input type="email" required
        placeholder="Email"
        name='email'
        onChange={inputEvent} 
            value={fullname.email}
        />
        <br/>
        <input type="number" required
        placeholder="Phone Number"
        name='phone'
        onChange={inputEvent} 
            value={fullname.phone}
        />
        <br/>
        <input type="password" required
        placeholder="Password (at least 8 character)"
        name='password'
        onChange={inputEvent} 
            value={fullname.password}
        />
        <br/>
        <input type="password" required
        placeholder="Confirm Password"
        name='confirmp'
        onChange={inputEvent} 
            value={fullname.confirmp}
        />
        <span className="notifiy">

        </span>
        <br/>
        <button type="submit">submit </button>
        </form>
    </div>
    <div class="circle c1"></div>
    <div class="circle c2"></div>
</div>

    </>
);
}
export default App;
